    def click(self):
        if self.downloading:
            return

        self.message.setText('')
        
        link = self.link.text().strip()
        start = self.start.text().strip().upper()
        end = self.end.text().strip().upper()
        print(link, start, end)

        if link == '':
            return
        
        if start == '' or len(start) != 10 or end == '' or len(end) != 10:
            return
        
        filename = QtWidgets.QFileDialog.getSaveFileName(None, 'Save Results', '', 'Excel Files (*.xlsx)')[0]
        
        if filename is None or filename == '':
            return
        
        if '.' not in filename:
            filename += '.xlsx'

        thread = threading.Thread(target=lambda: self.process(link, start, end, filename))
        thread.start()

    def process(self, link, start, end, filename):
        self.download = True
        self.message.setText('downloading...')

        isDone = download_results(link, start, end, filename)

        if isDone:
            self.message.setText('downloaded sucessfully')
        else:
            self.message.setText('downloaded failed')

        self.downloading = False

